let parser = new DOMParser(); // create a DOMParser object

frappe.call("estc_app.estc_hr.doctype.employee.employee.get_current_employee_leave_balance").then(r=>{

  root_element.querySelector('#max_leave').textContent= r.message.max_leave
  root_element.querySelector('#leave_usage').textContent= r.message.use_leave
  root_element.querySelector('#balance').textContent= r.message.balance

  r.message.leave_data.forEach((x)=>{

     root_element.querySelector("#leave_type_balance").appendChild(createProgress(x))


  })

})


function createProgress(d){
    d.max_leave = d.max_leave ==0?1:d.max_leave
    let ui = `
    <div>
        <strong>${d.leave_type} (${d.use_leave}/${d.max_leave})</strong>
        <div class="progress-container">
            <div class="progress" style="width: ${(d.use_leave / d.max_leave * 100) > 100 ? 100 : (d.use_leave / d.max_leave * 100)}%; background: ${d.color}">
                ${(d.use_leave/d.max_leave)*100}%
            </div>
        </div>
    <di><br/>`

    return parser.parseFromString(ui, "text/html").querySelector("div")

}

