script:
let parser = new DOMParser()
frappe.call("estc_app.estc_hr.doctype.employee.employee.get_recent_leave_request").then(r=>{
  r.message.forEach(x=>{
    root_element.querySelector("#my_leave_request").appendChild(get_leave(x))    
  })

})


    
 

function get_leave(data){
    let status = data.status
    let status_color=""
    if (status=="Request"){
        status =  "submitted for approval"
        status_color="orange"
    }else if(status=='Approved'){
        status_color="green"
    }else{
        status_color="red"
    }
    let to_date = ""
    if (data.to_date !=data.start_date){
        to_date = "to " + frappe.format(data.to_date,{fieldtype:"Date"})
    }
    
    let ui = `
    <div class='wrapper-my-info'>
        <div class='flex justify-content-between'>    
            <div class='d-flex'>
                <span class='mr-2' style='color:${status_color};font-size:16px;'>•</span>

                <span class="info-section">
                    <span class="text-dark">Leave request made on ${frappe.format(data.posting_date,{fieldtype:"Date"})} has been ${status}</span>
                    <div><strong>Leave Type:</strong><span> ${data.leave_type}</span></div>
                    <div><strong>Apply on:</strong> ${frappe.format(data.start_date,{fieldtype:"Date"})} ${to_date}</div>
                </span>
            </div>
            <div>
                <strong class='rounded-pill p-2 text-white' style='background:${status_color};font-size:12px;'>${data.status}</strong>
            </div>
        </div>
    <div>`
    let dom = parser.parseFromString(ui, "text/html").querySelector("div")
    dom.addEventListener('click', function() {
        frappe.set_route('Form', 'Leave Request',data.name)
    })
    return dom
}

