script:
let today = root_element.querySelector('#btn_today');
let late = root_element.querySelector('#btn_late');

today.classList.add('active');
$(root_element).find("#today").css("display", "block");

today.addEventListener('click', function(event) {
    tabHeaderClick(event,'today')
});

late.addEventListener('click', function(event) {
    tabHeaderClick(event,'late')
});

function set_align(element,value){
   sym=["฿","៛","$"]
   val_sym = value.toString().replace(' ','').replace(/[-,.0-9]/g, '')
   if (sym.includes(val_sym)){
      element.classList.add('text_align_right');
   }
   else if(val_sym == ""){
       element.classList.add('text_align_center');
   }
   else{
      element.classList.add('text_align_left');
   }
 }  

function tabHeaderClick(evt, doctype) {
  var i, tabcontent, tablinks;
  tabcontent = $(root_element).find('.tabcontent')
  for (i = 0; i < tabcontent.length; i++) {
    tabcontent[i].style.display = "none";
  }
  tablinks = $(root_element).find('.tablinks')
  for (i = 0; i < tablinks.length; i++) {
      
    tablinks[i].className = tablinks[i].className.replace(" active", "");
  }
  $(root_element).find("#" + doctype).css("display", "block");
  
  evt.currentTarget.className += " active";
}

