            let value = r.message.late[key];
            let th_currency = document.createElement("th");
            th_currency.classList.add('text_align_center');
            th_currency.textContent = key.replace(/_/g, ' ') 
            late_tr.appendChild(th_currency)
        }
        late_thead.appendChild(late_tr)
    }
    late_table.appendChild(late_thead);
    r.message.late.forEach((row)=>{
        let data_row = document.createElement("tr");
        let data_cell_invocie = document.createElement("td");
        let data_cell_date = document.createElement("td");
        data_cell_invocie.textContent=row.name;
        data_cell_date.textContent=row.payment_date;
        for (let key in row) {
            if (row.hasOwnProperty(key)) {
                let value = row[key];
                let data_cell_currency = document.createElement("td");
                set_align(data_cell_currency,value)
                data_cell_currency.textContent=value;
                data_row.appendChild(data_cell_currency);
            }
        }
        data_row.addEventListener('click', function() {
            frappe.route_options = {'loan': row.loan};
            frappe.set_route('Form', 'Loan Repayment','new-loan_repayment');
        });
        late_tbody.appendChild(data_row);
    });
})

today_table.appendChild(today_thead)
