script:
let tab = root_element.querySelector('#tab-head');
let tab_body = root_element.querySelector('#tab-body');
let leave_balances = []

frappe.call('estc_app.estc_hr.doctype.department.department.get_department').then((r)=>{
    frappe.call('estc_app.estc_hr.doctype.employee_attendance_leave_count.employee_attendance_leave_count.get_employee_leave_balance').then((resp)=>{
        leave_balances = resp.message
        
        r.message.forEach((department)=>{
                let department_button = document.createElement("button");
                department_button.classList.add("tablinks");
                department_button.id = "btn"+department.name.replace(" ", "-")
                department_button.textContent = department.name
                tab.appendChild(department_button);
                department_button.addEventListener('click', function(event) {
                    openTab(event,department.name)
                });
        
                let table    = document.createElement("table");
                let tbody = document.createElement("tbody");
                let thead = document.createElement("thead");
                let tr_head = document.createElement("tr");
                let th = document.createElement("th");
                let tab_content = document.createElement("div")
                th.textContent='Employee'
                //Loop Table Head
                for (let key in leave_balances[0]) {
                    if (leave_balances[0].hasOwnProperty(key) && key != 'department') {
                        let th_leave_type = document.createElement("th");
                        if (typeof leave_balances[0][key] === 'number'){
                           th_leave_type.classList.add('text-center')
                        }
                        
                        th_leave_type.textContent=key
                        tr_head.append(th_leave_type)
                    }
                }
                thead.append(tr_head)
                table.append(thead)
               
                leave_balances.forEach((balance)=>{
                    let data_row = document.createElement("tr");
                    if (balance.department == department.name){
                        for (let key in balance) {
                            if (balance.hasOwnProperty(key) && key != 'department') {
                               let data_cell = document.createElement("td");
                               if (typeof balance[key] === 'number'){
                                   data_cell.classList.add('text-center')
                               }
                               data_cell.textContent=balance[key]
                               data_row.append(data_cell)
                            }
                        }
                    tbody.append(data_row)
                    }
                })
                table.append(tbody)
                tab_content.append(table)
                tab_content.classList.add('tabcontent')
                tab_content.id = department.name.replace(" ", "-")
                tab_body.append(tab_content)
     })

    if (r.message.length > 0){
        first_tab_index="#btn"+r.message[0].name.replace(" ", "-")
        let active_tab = root_element.querySelector(first_tab_index);
        active_tab.classList.add('active');
        $(root_element).find("#" + r.message[0].name.replace(" ", "-")).css("display", "block");
    }
    })

     
     
     
    
})

function openTab(evt, department) {
  var i, tabcontent, tablinks;
  tabcontent = $(root_element).find('.tabcontent')
  for (i = 0; i < tabcontent.length; i++) {
    tabcontent[i].style.display = "none";
  }
  tablinks = $(root_element).find('.tablinks')
  for (i = 0; i < tablinks.length; i++) {
      
    tablinks[i].className = tablinks[i].className.replace(" active", "");
  }
  $(root_element).find("#" + department.replace(' ','-')).css("display", "block");
  
  evt.currentTarget.className += " active";
}

